CREATE TABLE IF NOT EXISTS `tpluginemailvorlage` (`kEmailvorlage` int(10) unsigned NOT NULL auto_increment,`kPlugin` int(10) unsigned NOT NULL default '0',`cName` varchar(255) default NULL,`cBeschreibung` text NOT NULL,`cMailTyp` varchar(255) NOT NULL default 'text/html',`cModulId` varchar(255) default NULL,`cDateiname` varchar(255) NOT NULL default '',`cAktiv` char(1) default NULL,`nAKZ` tinyint(3) unsigned NOT NULL,`nAGB` tinyint(3) unsigned NOT NULL,`nWRB` tinyint(3) unsigned NOT NULL,PRIMARY KEY  (`kEmailvorlage`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=22 ;
CREATE TABLE IF NOT EXISTS `tpluginemailvorlagesprache` (`kEmailvorlage` int(10) unsigned NOT NULL default '0',`kSprache` tinyint(3) unsigned NOT NULL default '0',`cBetreff` varchar(255) default NULL,`cContentHtml` text NOT NULL,`cContentText` text NOT NULL,`cPDFS` varchar(255) NOT NULL,`cDateiname` varchar(255) NOT NULL,PRIMARY KEY  (`kEmailvorlage`,`kSprache`)) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `tpluginemailvorlagespracheoriginal` (`kEmailvorlage` int(10) unsigned NOT NULL default '0',`kSprache` tinyint(3) unsigned NOT NULL default '0',`cBetreff` varchar(255) default NULL,`cContentHtml` text NOT NULL,`cContentText` text NOT NULL,`cPDFS` varchar(255) NOT NULL,`cDateiname` varchar(255) NOT NULL, PRIMARY KEY  (`kEmailvorlage`,`kSprache`)) ENGINE=MyISAM DEFAULT CHARSET=latin1;
